import { useState } from "react"
import Nav from "./Nav"
import axios from "axios"

const Login = () => {
    const [user , Uuser] = useState('')
    const [pass , Upass] = useState('')
    const [atype,updateType] = useState('danger')
    const [alert,ualert] = useState({ display: 'none'})
    const [message,umessage] = useState('')
    const sendMessage = (text,type='danger',time = 5) =>{
        updateType(type); umessage(text);ualert({display:'block'})
        setTimeout(() => {
          ualert({display:'none'})
          if(type === 'success') document.location.replace('/home')
        },time * 1000);
    }

    const submit = async (e) => {
  e.preventDefault();

  if (user.length < 3 || pass.length < 3) {
    sendMessage('Please fill the form correctly!!');
    return;
  }

  try {
    const response = await axios.post('http://localhost:5000/api/login', { user, pass }, {
      headers: {
        'Content-Type': 'application/json',
      },
    });

    const data = response.data;

    if (data.status) {
      sendMessage(data.message, 'success');
    } else {
      sendMessage(data.message);
    }
  } catch (error) {
    // Handle any errors that occur during the request.
    console.error('Error:', error);
    // You can also send an error message if needed.
    sendMessage('An error occurred during the request.');
  }
};

  return (
    <>
    <Nav />
    <div className="container mt-5">
    <div className={`alert alert-${atype}`} style={alert} role="alert">
      {message}
    </div>
        <div className="card">
            <div className="card-body">
            <form>
  <div className="mb-3">
    <label htmlfor="username" className="htmlform-label" >Username</label>
    <input type="text" className="form-control"  onChange={e => Uuser(e.target.value)} id="username" />
    
  </div>
  <div className="mb-3">
    <label htmlhtmlfor="password" className="form-label">Password</label>
    <input type="password" className="form-control" onChange={e => Upass(e.target.value)} id="password"/>
  </div>
  <button onClick={e => submit(e)} className="btn btn-primary">Login</button>
</form>
            </div>
        </div>
    </div>
    </>
)}

export default Login
