import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Notemaker.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const NoteMaker = () => {
  const [dataFromBackend, setDataFromBackend] = useState([]);
  const [newCard, setNewCard] = useState({ title: '', note: '', data: '' });
  const [categoryFilter, setCategoryFilter] = useState(''); 

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/getNotes");
      const { data } = response;
      setDataFromBackend(data);
    } catch (error) {
      console.error("Error fetching data from the backend:", error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewCard({
      ...newCard,
      [name]: value,
    });
  };

  const handleAddCard = () => {
  
    const newData = [...dataFromBackend, newCard];

  
    setDataFromBackend(newData);

    
    setNewCard({ title: '', note: '', data: '' });
  };

  const handleEditCard = (index) => {
   
  };

  const handleDeleteCard = (index) => {

    const newData = [...dataFromBackend];
    newData.splice(index, 1);
    setDataFromBackend(newData);
  };

  const handleCategoryFilterChange = (event) => {
   
    setCategoryFilter(event.target.value);
  };

  return (
    <div>
      <h1>Note Maker</h1>
      <div className="card-form">
        <input
          type="text"
          placeholder="Title"
          name="title"
          value={newCard.title}
          onChange={handleInputChange}
        />
        <textarea
          placeholder="Note"
          name="note"
          value={newCard.note}
          onChange={handleInputChange}
        ></textarea>
        <input
          type="text"
          placeholder="Category"
          name="data"
          value={newCard.data}
          onChange={handleInputChange}
        />
        <button onClick={handleAddCard}>Add Card</button>
      </div>
      <div className="filter-bar">
        <input
          type="text"
          placeholder="Filter by Category"
          value={categoryFilter}
          onChange={handleCategoryFilterChange}
        />
      </div>
      <div className="card-list">
        {dataFromBackend
          .filter((item) =>
            categoryFilter ? item.data.includes(categoryFilter) : true
          )
          .map((item, index) => (
            <div key={index} className="card">
              <h2>{item.title}</h2>
              <p>Note: {item.note}</p>
              <p>Category: {item.data}</p>
              <div className="card-icons">
                <span>
                  <FontAwesomeIcon
                    icon={faEdit}
                    onClick={() => handleEditCard(index)}
                    className="edit-icon"
                  />
                </span>
                <span>
                  <FontAwesomeIcon
                    icon={faTrash}
                    onClick={() => handleDeleteCard(index)}
                    className="delete-icon"
                  />
                </span>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default NoteMaker;
